import React, { useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonCard, IonCardHeader, IonCardSubtitle, IonCardTitle, IonCardContent, IonButtons, IonBackButton, IonButton, IonImg, IonPopover } from '@ionic/react';

import './Tab2.css';
import axios from 'axios';

const RequestToMyApi = () => {
  return axios({
    url: 'http://localhost:25000/perfiles',
    method: 'get'
  }).then(response => {
    return response.data;
  })
};

const Tab2: React.FC = () => {

  const [players, setPlayers] = React.useState([]);
  const [showPopover, setShowPopover] = useState(false);

  React.useEffect(() => {
    RequestToMyApi().then(data => setPlayers(data))
  }, []);

  const caca  = () => {
    return(
      <>
      <IonPopover
        isOpen={showPopover}
        cssClass='my-custom-class'
        onDidDismiss={e => setShowPopover(false)}
      >
        <p>This is popover content</p>
      </IonPopover>
      <IonButton onClick={() => setShowPopover(true)}>Show Popover</IonButton>
    </>
    );
  }



  return (
    <IonPage >
      <IonHeader>
        <IonToolbar>
            <IonButtons slot="start">
              <IonBackButton defaultHref='/' text='regresar' />
            </IonButtons>
          <IonTitle>Tab 2</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className='ion-padding' fullscreen>
       {players.map(({tipo, servidor, division,nombre}, i) => ( 

          <IonCard   key={i}>

              <IonImg className='img-perfil' src={"../assets/images/"+division+".png"}/>


          <IonCardHeader>
        <IonCardSubtitle className='ion-text-center'>{tipo}, {servidor}</IonCardSubtitle>
        <IonCardTitle className='ion-text-center'>{nombre}</IonCardTitle>
          </IonCardHeader>

            <IonCardContent className='ion-text-center'>
              {division}
            </IonCardContent>
          </IonCard>

       ))
       }
         <IonButton className='ion-margin' expand="block">Agregar perfil</IonButton>


      </IonContent>
    </IonPage>
  );
};

export default Tab2;
