import React, { useState } from "react";
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonButtons,
  IonBackButton,
  IonCard,
  IonImg,
  IonCardHeader,
  IonCardSubtitle,
  IonCardTitle,
  IonCardContent,
} from "@ionic/react";

import "./Tab3.css";
import axios from "axios";

const RequestToMyApi = () => {
  return axios({
    url: "http://localhost:25000/partidas",
    method: "get",
  }).then((response) => {
    return response.data;
  });
};

const Tab3: React.FC = () => {
  const [partidas, setPartida] = useState([]);
  const obj = {
    duracion: 32,
    id_campeon: 1,
    id_partida: 2,
    id_perfil: 0,
    winorlose: "win",
  };

  React.useEffect(() => {
    RequestToMyApi().then((data) => setPartida(data));
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref="/" text="regresar" />
          </IonButtons>
          <IonTitle>Tab 3</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        {partidas.map((partida) => (
          <IonCard key={partida['id_partida']}>
          <IonImg src={'/assets/images/'+partida['winorlose']+'.jpg'}/>

          <IonCardSubtitle className='ion-margin-top ion-text-center'>
            {'La partida durÃ³ '+partida['duracion']+" minutos"}
          </IonCardSubtitle>
          <IonCardTitle className='ion-text-center ion-text-capitalize'>
            {partida['winorlose']}
          </IonCardTitle>

          <IonCardContent className='ion-text-center'>
            {partida['fechayhora']}
          </IonCardContent>
        </IonCard>
        ))}

<IonPopover
        isOpen={showPopover}
        cssClass='my-custom-class ion-padding'
        onDidDismiss={e => setShowPopover(false)}

      >

        <IonItem >
            <IonInput  value={tipo} placeholder="Tipo de perfil" onIonChange={e => setTipo(e.detail.value!)}></IonInput>
        </IonItem>
        <IonItem >
            <IonInput  value={server} placeholder="Servidor" onIonChange={e => setServer(e.detail.value!)}></IonInput>
        </IonItem>
        <IonItem >
            <IonInput  value={nombre} placeholder="Nombre" onIonChange={e => setNombre(e.detail.value!)}></IonInput>
        </IonItem>
        <IonItem >
            <IonInput  value={div} placeholder="Division" onIonChange={e => setDiv(e.detail.value!)}></IonInput>
        </IonItem>

        <IonButton className='ion-margin'  onClick={Agregar}>Agregar</IonButton>

      </IonPopover>
      <IonButton expand='block' onClick={() => setShowPopover(true)}>Agregar perfil</IonButton>

      </IonContent>
    </IonPage>
  );
};

export default Tab3;
