import React, { useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonCard, IonCardHeader, IonCardSubtitle, IonCardTitle, IonCardContent, IonButtons, IonBackButton, IonButton, IonImg, IonPopover, IonItem, IonInput } from '@ionic/react';

import './Tab2.css';
import axios from 'axios';

const RequestToMyApi = () => {
  return axios({
    url: 'http://localhost:25000/perfiles',
    method: 'get'
  }).then(response => {
    return response.data;
  })
};

const Tab2: React.FC = () => {

  const [players, setPlayers] = React.useState([]);
  const [showPopover, setShowPopover] = useState(false);
  const [text, setText] = useState<string>();

  React.useEffect(() => {
    RequestToMyApi().then(data => setPlayers(data))
  }, []);




  return (
    <IonPage >
      <IonHeader>
        <IonToolbar>
            <IonButtons slot="start">
              <IonBackButton defaultHref='/' text='regresar' />
            </IonButtons>
          <IonTitle>Tab 2</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className='ion-padding' fullscreen>
       {players.map(({tipo, servidor, division,nombre}, i) => ( 

          <IonCard   key={i}>

              <IonImg className='img-perfil' src={"../assets/images/"+division+".png"}/>


          <IonCardHeader>
        <IonCardSubtitle className='ion-text-center'>{tipo}, {servidor}</IonCardSubtitle>
        <IonCardTitle className='ion-text-center'>{nombre}</IonCardTitle>
          </IonCardHeader>

            <IonCardContent className='ion-text-center'>
              {division}
            </IonCardContent>
          </IonCard>

       ))
       }
         <IonPopover
        isOpen={showPopover}
        cssClass='my-custom-class'
        onDidDismiss={e => setShowPopover(false)}
      >

        <IonItem>
            <IonInput className='marginxd' value={text} placeholder="Enter Input" onIonChange={e => setText(e.detail.value!)}></IonInput>
        </IonItem>

      </IonPopover>
      <IonButton expand='block' onClick={() => setShowPopover(true)}>Agregar perfil</IonButton>


      </IonContent>
    </IonPage>
  );
};

export default Tab2;
