import React, { Fragment, useState } from 'react'
import { IonPage, IonHeader, IonToolbar, IonButtons, IonBackButton, IonTitle, IonContent, IonButton, IonPopover, IonItem, IonInput, IonCard, IonCardContent, IonCardTitle } from '@ionic/react';
import axios from 'axios';


export const Consultas = () => {
  const promedioPartida = () => {
    return axios({
      url: 'http://localhost:25000/avgp_dates',
       method: 'post',
       headers: {}, 
      data: {
        'date1': '2020-01-06',
        'date2':  '2020-12-21',
    }
     }).then(response => {
       return response.data;
     })
   };

   

   //Resultados
    const [resultado1, setResultado1] = useState('')
    const [resultado2, setResultado2] = useState('')
    
    
    //Promedio duracion partida
    const [fecha1, setFecha1] = useState('')
    const [fecha2, setFecha2] = useState('')

   promedioPartida().then(data => console.log(data['avg'].toString()))

    const [showPopover, setShowPopover] = useState(false);

    const Consult = () => {
      setShowPopover(false);
      
      setResultado1('Promedio de duraciÃ³n de partida entre '+fecha1+' y'+fecha2);
      
        promedioPartida().then(data => setResultado2(data));
  
     
      
    }
    
    

    return (
        <IonPage>
      <IonHeader>
        <IonToolbar>

            <IonButtons slot="start">
              <IonBackButton defaultHref='/' text='regresar' />
            </IonButtons>
          <IonTitle>Consultas</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>

        <IonButton onClick={() => setShowPopover(true)} className='ion-margin' expand="full" >
            Promedio de duraciÃ³n de partida <br/>en un rango de fecha
        </IonButton>

              <IonPopover
                  isOpen={showPopover}
                  cssClass='my-custom-class ion-padding'
                  onDidDismiss={e => setShowPopover(false)}
                
                >
                  <IonItem>
                    <IonInput  value={fecha1} placeholder="AAAA-MM-DD" onIonChange={e => setFecha1(e.detail.value!)}></IonInput>
                  </IonItem>
                  <IonItem >
                    <IonInput  value={fecha2} placeholder="AAAA-MM-DD" onIonChange={e => setFecha2(e.detail.value!)}></IonInput>
                  </IonItem>

                  <IonButton className='ion-margin'  onClick={Consult}>Agregar</IonButton>
              </IonPopover>
           

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Campeon mÃ¡s utilizado por los profesionales
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Items mÃ¡s utilizados por un campeon
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Runas mÃ¡s utilizadas por un campeon
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Campeon mÃ¡s jugado en un rango de tiempo
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Campeon mÃ¡s jugado por los demÃ¡s <br/> en un rango de tiempo
        </IonButton>


        <IonCard>
          <IonCardTitle>
            {resultado1}
          </IonCardTitle>

          <IonCardContent>
            {resultado2}
          </IonCardContent>
        </IonCard>
        
      </IonContent>
    </IonPage>
    )
}
