import React, { Fragment, useState } from 'react'
import { IonPage, IonHeader, IonToolbar, IonButtons, IonBackButton, IonTitle, IonContent, IonButton, IonPopover, IonItem, IonInput } from '@ionic/react';
import axios from 'axios';


export const Consultas = () => {
  const promedioPartida = () => {
    return axios({
      url: 'http://localhost:25000/avgp_dates',
       method: 'post',
       headers: {}, 
      data: {
        'date1': '26-11-2020',
        'date2': '07-12-2020',
    }
     }).then(response => {
       return response.data;
     })
   };

   promedioPartida().then(data => console.log(data));


    
    
