    const [fecha2, setFecha2] = useState('')

    const [showPopover, setShowPopover] = useState(false);

    const Consult = () => {
      setShowPopover(true);
      
    }
    React.useEffect(() => {
        
        
    }, []);

    

    return (
        <IonPage>
      <IonHeader>
        <IonToolbar>

            <IonButtons slot="start">
              <IonBackButton defaultHref='/' text='regresar' />
            </IonButtons>
          <IonTitle>Consultas</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>

        <IonButton onClick={() => setShowPopover(true)} className='ion-margin' expand="full" >
            Promedio de duración de partida <br/>en un rango de fecha
        </IonButton>

              <IonPopover
                  isOpen={showPopover}
                  cssClass='my-custom-class ion-padding'
                  onDidDismiss={e => setShowPopover(false)}
                
                >
                  <IonItem>
                    <IonInput  value={fecha1} placeholder="DD-MM-AAAA" onIonChange={e => setFecha1(e.detail.value!)}></IonInput>
                  </IonItem>
                  <IonItem >
                    <IonInput  value={fecha2} placeholder="DD-MM-AAAA" onIonChange={e => setFecha2(e.detail.value!)}></IonInput>
                  </IonItem>

                  <IonButton className='ion-margin'  onClick={Agregar}>Agregar</IonButton>
              </IonPopover>
           

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Campeon más utilizado por los profesionales
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Items más utilizados por un campeon
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Runas más utilizadas por un campeon
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Campeon más jugado en un rango de tiempo
        </IonButton>

        <IonButton onClick={Consult} className='ion-margin' expand="full" >
            Campeon más jugado por los demás <br/> en un rango de tiempo
        </IonButton>
        
      </IonContent>
    </IonPage>
    )
}
